<?phprequire_once (dirname( __FILE__ ) . '\..\include\constants.inc.php');class Database {    private $dbConnection;          // MySQL link identifer    /* Database     * Class constructor     */    public function Database() {        $this->dbConnect();    }    /*     * dbConnect     * Connect to MySQL database, script dies if failed.     */    private function dbConnect() {        $this->dbConnection = mysql_connect(DB_HOST, DB_USER, DB_PASS)            OR die('Could not connect to database : ' . mysql_error());        mysql_select_db(DB_NAME)            OR die ("Could not select database : " . mysql_error());    }    /*     * dbDisconnect     * Closes database connection     */    public function dbDisconnect() {        mysql_close($this->dbConnection);    }}?>